mapper.sql:
id ,u.user_id userId ,service_type serviceType ,service_content serviceContent ,settle_time settleTime ,salary_type salaryType ,amount ,title ,des ,content ,requirement ,place ,req_sex reqSex ,req_identity reqIdentity ,req_edu reqEdu ,req_age reqAge ,req_num reqNum ,sign_num signNum ,valid_date validDate ,u.create_time createTime ,create_user createUser ,u.modify_time modifyTime ,modify_user modifyUser ,u.current_state currentState ,remark ,(SELECT current_state FROM t_project_enrollment b WHERE b.project_id=u.id AND b.user_id=#{project.signUser}) signState ,c.username createUserName ,u.open_state openState FROM t_project u left join t_user c on u.user_id=c.user_id WHERE 1=1
AND u.service_type = #{project.serviceType}
AND u.settle_time = #{project.settleTime}
AND u.req_sex = #{project.reqSex}
AND u.req_edu = #{project.reqEdu}
AND u.place = #{project.place}
AND u.user_id = #{project.userId}
AND u.current_state = #{project.currentState}
AND u.id = #{project.id}

