SELECT id ,user_id userId ,service_type userType ,service_content serviceContent ,settle_time settleTime ,salary_type salaryType ,amount ,title ,des ,content ,requirement ,place ,req_sex reqSex ,req_identity reqIdentity ,req_edu reqEdu ,req_age reqAge ,req_num reqNum ,valid_date validDate ,create_time createTime ,create_user createUser ,modify_time modifyTime ,modify_user modifyUser ,current_state currentState ,remark FROM t_project u WHERE 1 = 1
AND u.service_type = #{project.serviceType}
AND u.settle_time = #{project.settleTime}
AND u.req_sex = #{project.reqSex}
AND u.req_edu = #{project.reqEdu}
AND u.place = #{project.place}
