mapper.sql:
SELECT u.user_id userId, u.username, u.email, u.mobile, u.status, u.create_time createTime, u.ssex sex, u.dept_id deptId, u.last_login_time lastLoginTime, u.modify_time modifyTime, u.description, u.avatar, d.dept_name deptName, GROUP_CONCAT(r.role_id) roleId, GROUP_CONCAT(r.ROLE_NAME) roleName FROM t_user u LEFT JOIN t_dept d ON (u.dept_id = d.dept_id) LEFT JOIN t_user_role ur ON (u.user_id = ur.user_id) LEFT JOIN t_role r ON r.role_id = ur.role_id WHERE 1 = 1
AND u.username = #{user.username}
AND d.dept_id = #{user.deptId}
AND u.ssex = #{user.sex}
AND u.status = #{user.status}
AND u.mobile = #{user.mobile}
And u.create_time > #{user.createTimeFrom}
And u.create_time < #{user.createTimeTo}
group by u.username
